
import BaseConnection = require('mysql2/typings/mysql/lib/Connection');
import {ConnectionOptions, SslOptions} from 'mysql2/typings/mysql/lib/Connection';
import BasePoolConnection = require('mysql2/typings/mysql/lib/PoolConnection');
import BasePool = require('mysql2/typings/mysql/lib/Pool');
import {PoolOptions} from 'mysql2/typings/mysql/lib/Pool';
import BasePoolCluster = require('mysql2/typings/mysql/lib/PoolCluster');
import {PoolClusterOptions} from 'mysql2/typings/mysql/lib/PoolCluster';
import BaseQuery = require('mysql2/typings/mysql/lib/protocol/sequences/Query');
import {QueryOptions, StreamOptions, QueryError} from 'mysql2/typings/mysql/lib/protocol/sequences/Query';

export function createConnection(connectionUri: string): Connection;
export function createConnection(config: BaseConnection.ConnectionOptions): Connection;
export function createPool(config: BasePool.PoolOptions): Pool;
export function createPoolCluster(config?: BasePoolCluster.PoolClusterOptions): PoolCluster;
export function escape(value: any): string;
export function format(sql: string): string;
export function format(sql: string, values: any[]): string;
export function format(sql: string, values: any): string;

export {
    ConnectionOptions,
    SslOptions,
    PoolOptions,
    PoolClusterOptions,
    QueryOptions,
    QueryError
};
export * from 'mysql2/typings/mysql/lib/protocol/packets';

// Expose class interfaces
export interface Connection extends BaseConnection {}
export interface PoolConnection extends BasePoolConnection {}
export interface Pool extends BasePool {}
export interface PoolCluster extends BasePoolCluster {}
export interface Query extends BaseQuery {}
